package cha2;

public class BinaryTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 정수
		int num = 10;
		//0B 2진수 
		int bNum = 0B1010; //10
		//0 8진수
		int oNum = 012;
		//0X 16진수
		int hNum = 0XA;
		
		System.out.println(num);
		System.out.println(bNum);
		System.out.println(oNum);
		System.out.println(hNum);
	}

}
